import React, { useState } from 'react';
import './App.css';
import axios from 'axios';

function App() {
  // State variables for the two text areas
  const [text1Value, setText1Value] = useState('');
  const [text2Value, setText2Value] = useState('');

  const handleText1Change = (event) => {
    setText1Value(event.target.value);
  };

  const handleText2Change = (event) => {
    setText2Value(event.target.value);
  };

  const handleSubmit = async () => {
    try {
      // Send the data to the backend server
      await axios.post(`http://localhost:${process.env.PORT}/ideas`, {
        text1: text1Value,
        text2: text2Value,
      });
      // Clear the text areas after successful submission
      setText1Value('');
      setText2Value('');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className='app'>
      {/* ... */}
      <textarea
        className='input-textarea w-full max-h-max px-5 py-5 font-sans'
        type='textarea'
        placeholder='zendegi e shakhsi'
        value={text1Value}
        onChange={handleText1Change}
      />
      <br />
      <textarea
        className='input-textarea w-full max-h-max px-5 py-5 font-sans'
        type='textarea'
        placeholder='zendegi e herfeE'
        value={text2Value}
        onChange={handleText2Change}
      />

      <button type="submit" className="sendButton px-10 py-5 w-20" onClick={handleSubmit}>
        SEND
      </button>
    </div>
  );
}

export default App;
